import React, { useState, useEffect } from 'react';
import { useRouteMatch } from 'react-router';
import NavItem from './NavItem';
import './Navbar.css';

export default function Navbar() {
  const match = useRouteMatch();
  const [active, setActive] = useState({about: true, contact: false, portfolio: false});
  console.log('match :>> ', match);
  useEffect(() => {
    if (match.url.includes('/portfolio')) {
      setActive({about: false, contact: false, portfolio: false});
    } else if (match.url.includes('/contact')) {
      setActive({about: false, contact: true, portfolio: false});
    } else {
      setActive({about: true, contact: false, portfolio: false});
    }
  }, [match]);
  console.log('active :>> ', active);
  console.log(active === 'portfolio');
  return (
    <nav className='navbar navbar-expand-md'>
      <a className='nav-item active first-name' href='/'>
        PAUL KIRKPATRICK
      </a>
      <button
        className='navbar-toggler'
        data-toggle='collapse'
        data-target='#navbarSupportedContent'
        aria-controls='navbarSupportedContent'
        aria-expanded='false'
        aria-label='Toggle navigation'
      >
        <span className='fa fa-bars'></span>
      </button>
      <div className='collapse navbar-collapse' id='navbarSupportedContent'>
        <ul className='navbar-nav ml-auto'>
          <NavItem active={active === 'about'} href='/'>
            ABOUT ME
          </NavItem>
          <NavItem active={active === 'portfolio'} href='/portfolio'>
            PORTFOLIO
          </NavItem>
          <NavItem active={active === 'contact'} href='/contact'>
            CONTACT
          </NavItem>
        </ul>
      </div>
    </nav>
  );
}
